components/cart/status-messages:
{{#if cart.status_messages.length '>' 0}}
    <div class="cart-status">
        {{#each cart.status_messages}}
            <div class="alertBox alertBox--info">
                <div class="alertBox-column alertBox-message">
                    {{{message}}}
                </div>
            </div>                    
        {{/each}}
    </div>
{{/if}}
